

def find_floor(directions):
    floor = 0
    for action in directions:
        floor += decode_action(action)
    return floor

def find_basement(directions):
    floor = 0
    for i, action in enumerate(directions, 1):
        floor += decode_action(action)
        if floor == -1:
            return i
    return -1

def decode_action(action):
    if action == '(':
        return 1
    elif action == ')':
        return -1
    else:
        return 0
    
